# Distances for directions: [North, West, South, East]distances = [5, 3, 2, 4]# Starting pointx, y = 0, 0# Use range(len(distances)) to loop with indexesfor i in range(len(distances)):    distance = distances[i]        if i == 0:   # North        y += distance    elif i == 1: # West        x -= distance    elif i == 2: # South        y -= distance    elif i == 3: # East        x += distanceprint(f"Final position: ({x}, {y})")